import { useState, useEffect } from 'react';
import styles from './App.module.scss';
import { Header } from './components/header/header';
import { Footer } from './components/footer/footer';
import { Card } from './components/card/card';
import { MainCard } from './components/main-card/main-card';

interface Day {
    "date": number,
    "weather": string,
    "temp2m": {
    "max": number,
    "min": number
    },
    "wind10m_max": number
}

function App() {
    const [location, setLocation] = useState<any>(null)
    const [data, setData] = useState<Day[]>([])
    const getLocation = () => {
        if (!navigator.geolocation){
            console.log('Location APi is not supported by your browser')
        } else{
            navigator.geolocation.getCurrentPosition(position => {
                setLocation(position.coords)
            }, 
            () => console.log(`Sorry, we canot find your location`)
            )
        }
    }

    const fetchData = async() => {
        const latitude = location?.latitude
        const longitude = location?.longitude
         try{
            const response = await fetch(`https://www.7timer.info/bin/api.pl?lon=${longitude}&lat=${latitude}&product=civillight&output=json`)
            const data = await response.json()
            setData(data.dataseries)
         }catch (error){
            console.error(error)
         }
    }

    useEffect(() =>{
        getLocation()
        fetchData()
    }, [])

    console.log(data)

    const getImage = (weather: string) => {
        let image = ""
        let conditions = ""
        console.log(weather)
        switch(weather){
            case "clear":
                conditions = "Sunny"
                image = "data:image/png;base64,"
            break;
            case "mcloudy":
                conditions = "Cloudy"
                image = "data:image/png;base64,"
            break;
            case "ishower":
                conditions = "Rainy"
                image = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQnC96FDe4Q47AVb3GbQtJwGV6Jm_f6e9bIHLHxv__sqjBgvc_xCRRUP4QgdjE3MuvY1UY&usqp=CAU"
            break;
            case "storm":
                conditions = "Stormy"
                image = "data:image/png;base64,"
            break;
            default:
            conditions = "Unknown"
            image = "data:image/png;base64,"
        }
        return {conditions, image}
    }

    return (
        <div className={styles.App}>
            <Header />
            {data.length > 0 && data[0].temp2m && (
            <MainCard 
                temp={data?.slice(0,1)[0].temp2m.max}
                img={getImage(data?.slice(0,1)[0].weather).image}
            />
            )}
            <div className={styles['card-container']}>
                {data?.slice(1,4).map((day, _index) => <Card 
                    key={_index} 
                    day={day} 
                    img={getImage(day?.weather).image}
                    cond={getImage(day?.weather).conditions}
                    />)}
            </div>
            <Footer />
        </div>
    );
}

export default App;
